import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'
import {useTransition, animated} from 'react-spring'

function Navigation(){
    //use a react hook to give the icon a state

    // menu = <div className = "fixed bg-white top-0 left-0 w-4/5 h-full z-50 shadow">
    // menuMask = <div className="bg-black-t-50 fixed top-0 left-0 w-full h-full z-50"

    const [showMenu, setShowMenu] = useState(false)

    const transitions = useTransition(showMenu, null, {
        from: { position: 'absolute', opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 },
        })

    return(
        <nav>
            <span className="text-xl">
                <FontAwesomeIcon 
                icon = {faCoffee}
                onClick = { () => setShowMenu(!showMenu)}
                />
            </span>
            {
                transitions.map(({ item, key, props }) =>
                    item && <animated.div key={key} style={props}>✌️</animated.div>
                )
            }

        </nav>
    )
}

export default Navigation
